clock = () => {
  let d = new Date(),
      t = $id('time');
      hm = d.toLocaleTimeString('fr-FR', {hour: '2-digit',minute: '2-digit'}),
      ymd = d.toLocaleDateString('fr-FR', {weekday: 'long', day: 'numeric', month: 'long', year: 'numeric' });
  t.innerHTML = hm;
  t.setAttribute('title', $id('time'));
};
